import React, {useState} from "react";
import $ from 'jquery';

function Body() {

    const [isMouseInside, setState] = useState(false)

    console.log(isMouseInside);

    function mouseEnter(e){
        return setState(true);
    };

    function mouseLeave(e){
        return setState(false);
    };

    return (
      <div>

        <div class="title">
            <h2>Japanese Calligraphy by Rina</h2>
        </div>

        <form action="/details" method="post">
            <div class="grid">
                <div class="square" onMouseEnter={mouseEnter} onMouseLeave={mouseLeave}>
                    {[isMouseInside] ? null : <img class="up-right" src="/images/engraving.jpg"/>}
                    {[isMouseInside] ?  <button type="submit" class="btn btn-info" name="details" value="details1" id="details">Details</button> : null}
                </div>
                <div class="square" onMouseEnter={mouseEnter} onMouseLeave={mouseLeave}>
                    {[isMouseInside] ? null : <img class="up-right" src="/images/poet.jpg"/>}  
                    {[isMouseInside] ? <button type="submit" class="btn btn-info" name="details" value="details2" id="details">Details</button> : null}
                </div>
                <div class="square" onMouseEnter={mouseEnter} onMouseLeave={mouseLeave}>
                    {[isMouseInside] ? null : <img class="side-ways" src="/images/fam_name.jpg"/>}  
                    {[isMouseInside] ? <button type="submit" class="btn btn-info" name="details" value="details3" id="details">Details</button> : null}
                </div>
                <div class="square" onMouseEnter={mouseEnter} onMouseLeave={mouseLeave}>
                    {[isMouseInside] ? null : <img class="side-ways" src="/images/miso.png"/>}  
                    {[isMouseInside] ?  <button type="submit" class="btn btn-info" name="details" value="details4" id="details">Details</button> : null}
                </div>
            </div>
        </form>

        <script src="https://ajax.googleapis.com/ajax/libs/jquery/3.6.1/jquery.min.js"></script>
        {/* <script>

            $(".square").on('mouseenter', function(e){
                $(this).find("img").hide();
                $(this).find(":button").show();
            }).on('mouseleave', function(e){
                $(this).find(":button").hide();
                $(this).find("img").show();
            });

        </script> */}

      </div>

    );
  }

export default Body;
